import Foundation
import CRC

final class CRC64Tests: CRCTestCase<UInt64> {

    func testCalculatorASCII123456789() {
        verifyCalculator(for: "123456789".data(using: .ascii)!, with: [.ecma, .iso])
    }

    /// Source: https://toolkitbay.com/tkb/tool/CRC-64
    func testASCII123456789() {
        verify(for: "123456789".data(using: .ascii)!, expected: [
            (0x995DC9BBDF1939FA, .ecma),
            (0xB90956C775A41001, .iso),
        ])
    }

    /// Source: https://toolkitbay.com/tkb/tool/CRC-64
    func test0x0204() {
        verify(for: "987654321".data(using: .ascii)!, expected: [
            (0xE7ADEF3D663C7E22, .ecma),
            (0x3CAFB00415A776CF, .iso),
        ])
    }

